package sistema.visao.campanha;import java.util.ArrayList;import java.util.List;import javax.faces.model.SelectItem;import javax.inject.Inject;import javax.inject.Named;import org.springframework.context.annotation.Scope;import sistema.infra.formularios.GListaDetalheFrm;import sistema.negocio.aplicacao.campanha.CampanhaBean;import sistema.negocio.aplicacao.entidade.EntidadeBean;import sistema.negocio.dominio.Campanha;import sistema.negocio.dominio.entidade.Entidade;import sistema.negocio.enums.TipoEntidade;import com.forj.cirrus.infra.spring.Escopo;/** * Gerenciador de formulários GUI JSF de manutenção de <b>Campanha</b>. *  * @version 1.0 - 16/05/2016 * @since 16/05/2016 */@Named@Scope(Escopo.SESSION)public class CampanhaFrm extends GListaDetalheFrm<Campanha> {	/** Armazena o gerenciador dos processos de negócio. **/	@Inject	private CampanhaBean campanhaBean;	/** Armazena o nome da entidade para filtragem. **/	private String descricao;	/** Construtor padrão. **/	public CampanhaFrm() {		titulo = "Campanha";		dominio = new Campanha();	}	/** Entra no detalhe executando as operações indicadas. */	public void prepararNovo() {		System.out.println("Preparou novo");	}	/** {@inheritDoc} */	@Override	public String novo() {		return "detalhe.xhtml";	}	/** {@inheritDoc} */	@Override	public void pesquisar() {		System.out.println("Pesquisou");	}	public void salvar() {		System.out.println("Salvou");	}	public String getDescricao() {		return descricao;	}	public void setDescricao(String descricao) {		this.descricao = descricao;	}}