package sistema.visao.cargoadministrativoimport javax.annotation.Resourceimport javax.faces.model.SelectItem;import javax.inject.Namedimport org.springframework.context.annotation.Scopeimport sistema.infra.formularios.GListaDetalheFrmimport sistema.negocio.dominio.CargoAdministrativoimport sistema.negocio.dominio.CargoEclesiasticoimport sistema.negocio.dominio.entidade.Entidade;import com.forj.cirrus.infra.spring.Escopo;import com.forj.cirrus.negocio.aplicativo.DominioBeanimport com.forj.cirrus.util.Cirrus;/** * Gerenciador de formulários GUI JSF de manutenção de <b>CargoAdministrativo</b>. * @author Lucas Francisquini * @version 1.0 - 27/11/2015 * @since 27/11/2015 */@Named
@Scope(Escopo.SESSION)
class CargoAdministrativoFrm extends GListaDetalheFrm<CargoAdministrativo> {    /** Armazena o serviço de negocio. **/    @Resource(name = Cirrus.FACADE)    DominioBean<CargoAdministrativo> bean    /** Contrutor padrão. **/    CargoAdministrativoFrm() {        titulo = "Manutenção de Cargo Administrativo"        dominio = new CargoAdministrativo()    }    /** {@inheritDoc} */    @Override    public void pesquisar() {        try {            colecao = bean.get(CargoAdministrativo.TODOS)        } catch (ex) {            gerarErrosRia(ex)        }    }}