package sistema.visao.entidade;import java.util.ArrayList;import java.util.List;import javax.faces.model.SelectItem;import javax.inject.Inject;import javax.inject.Named;import org.springframework.context.annotation.Scope;import sistema.infra.formularios.GListaDetalheFrm;import sistema.negocio.aplicacao.entidade.EntidadeBean;import sistema.negocio.dominio.entidade.Entidade;import sistema.negocio.enums.TipoEntidade;import com.forj.cirrus.infra.exceptions.NegocioException;import com.forj.cirrus.infra.spring.Escopo;/** * Gerenciador de formulários GUI JSF de manutenção de <b>Entidade</b>. *  * @version 1.0 - 16/05/2016 * @since 16/05/2016 */@Named@Scope(Escopo.SESSION)public class EntidadeFrm extends GListaDetalheFrm<Entidade> {	/** Listagem do combo de seleção do status. **/	private List<SelectItem> cmboTipoIgreja;	/** Armazena o gerenciador dos processos de negócio. **/	@Inject	private EntidadeBean entidadeBean;	/** Armazena o nome da entidade para filtragem. **/	private String nomeEntidade;	/** Contrutor padrão. **/	public EntidadeFrm() {		titulo = "Entidade";		dominio = new Entidade();		entrar();	}	/** Entra no detalhe executando as operações indicadas. */	private void prepararNovo() {		cmboTipoIgreja = new ArrayList<>();		preencherCmboTipoIgreja();	}	/** Preenche a combo de tipos de igreja. */	private void preencherCmboTipoIgreja() {		cmboTipoIgreja.add(new SelectItem(TipoEntidade.M, TipoEntidade				.getInstance(TipoEntidade.M)));		cmboTipoIgreja.add(new SelectItem(TipoEntidade.F, TipoEntidade				.getInstance(TipoEntidade.F)));	}	/** {@inheritDoc} */	@Override	public void pesquisar() {		try {			colecao = entidadeBean.get();		} catch (NegocioException e) {			gerarErrosRia(e);		}	}	public List<SelectItem> getCmboTipoIgreja() {		return cmboTipoIgreja;	}	public String getNomeEntidade() {		return nomeEntidade;	}}