package sistema.visao.login;import javax.inject.Inject;import javax.inject.Named;import org.springframework.context.annotation.Scope;import sistema.infra.formularios.GListaDetalheFrm;import sistema.negocio.aplicacao.LoginBean;import sistema.negocio.dominio.entidade.Entidade;import com.forj.cirrus.infra.exceptions.NegocioException;import com.forj.cirrus.infra.spring.Escopo;/** * Gerenciador de formulários GUI JSF de manutenção de <b>Entidade</b>. *  * @version 1.0 - 16/05/2016 * @since 16/05/2016 */@Named@Scope(Escopo.SESSION)public class LoginFrm extends GListaDetalheFrm<Entidade> {	/** Armazena o gerenciador dos processos de negócio. **/	@Inject	private LoginBean loginBean;	private String user;	private String pass;	/** Construtor padrão. **/	public LoginFrm() {	}	public String acessar() {		try {			return loginBean.logar(user, pass);		} catch (NegocioException e) {			gerarErrosRia(e);		}		return "index.faces";	}	public String getUser() {		return user;	}	public void setUser(String user) {		this.user = user;	}	public String getPass() {		return pass;	}	public void setPass(String pass) {		this.pass = pass;	}}