package sistema.visao.membroimport javax.annotation.Resourceimport javax.faces.model.SelectItem;import javax.inject.Namedimport org.springframework.context.annotation.Scopeimport sistema.infra.formularios.GListaDetalheFrmimport sistema.negocio.dominio.Congregacaoimport sistema.negocio.dominio.Membroimport com.forj.cirrus.infra.spring.Escopo;import com.forj.cirrus.negocio.aplicativo.DominioBeanimport com.forj.cirrus.negocio.dominio.servicos.Dominio;import com.forj.cirrus.util.Cirrus;/** * Gerenciador de formulários GUI JSF de manutenção <b>Igreja</b>. * @author Lucas Francisquini * @version 1.0 - 26/11/2015 * @since 26/11/2015 */@Named
@Scope(Escopo.SESSION)
class MembroFrm extends GListaDetalheFrm<Membro> {    /** Armazena opções de seleção para sexo. **/    static final SelectItem[] sexo = [        new SelectItem("M", "Masculino"),        new SelectItem("F", "Feminino")    ]    /** Armazena opções de seleção para status. **/    static final SelectItem[] status = [        new SelectItem("A", "Ativo"),        new SelectItem("I", "Inativo")    ]    /** Armazena o serviço de negocio. **/    @Resource(name = Cirrus.FACADE)    DominioBean<Membro> bean    /** Contrutor padrão. **/    MembroFrm() {        titulo = "Manutenção de Membro"        dominio = new Membro()    }    /** {@inheritDoc} */    @Override    public void pesquisar() {        try {            colecao = bean.get(Membro.TODOS)        } catch (ex) {            gerarErrosRia(ex)        }    }}