package sistema.visao.secretariaimport javax.annotation.Resourceimport javax.inject.Namedimport org.springframework.context.annotation.Scopeimport sistema.infra.formularios.GListaDetalheFrmimport sistema.negocio.dominio.MembroAdministrativoimport sistema.negocio.dominio.Secretariaimport com.forj.cirrus.infra.spring.Escopoimport com.forj.cirrus.negocio.aplicativo.DominioBeanimport com.forj.cirrus.util.Cirrus
/** * Gerenciador de formulários GUI JSF de manutenção <b>Secretaria</b>. * @author Lucas Francisquini * @version 1.0 - 27/11/2015 * @since 27/11/2015 */@Named
@Scope(Escopo.SESSION)
class SecretariaFrm extends GListaDetalheFrm<Secretaria> {

    /** Armazena o serviço de negocio. **/    @Resource(name = Cirrus.FACADE)    DominioBean<Secretaria> bean    /** Armazena o cadastro de item. **/    MembroAdministrativo membroAdm;    /** Contrutor padrão. **/    SecretariaFrm() {        titulo = "Manutenção de Secretaria"        dominio = new Secretaria()        membroAdm = new MembroAdministrativo()    }    /** {@inheritDoc} */    @Override    void pesquisar() {        try {            colecao = bean.get(Secretaria.TODOS)        } catch (ex) {            gerarErrosRia(ex)        }    }    /** {@inheritDoc} */    @Override    String novo() {        membroAdm.limpar()        super.novo()    }    /** {@inheritDoc} */    @Override    String alterar(Secretaria nota) {        membroAdm.limpar()        super.alterar(nota)    }    /** Adiciona um item. **/    void adicionar() {        try {            dominio.adicionar(membroAdm)            membroAdm = new MembroAdministrativo();        } catch (ex) {            gerarErrosRia(ex)        }    }    /** Remove um membro administrativo. **/    void remover(MembroAdministrativo objeto) {        try {            dominio.deletar(objeto)        } catch (ex) {            gerarErrosRia(ex)        }    }}