package sistema.visao.tipoeventoimport javax.annotation.Resourceimport javax.faces.model.SelectItem;import javax.inject.Namedimport org.springframework.context.annotation.Scopeimport sistema.infra.formularios.GListaDetalheFrmimport sistema.negocio.dominio.CargoEclesiasticoimport sistema.negocio.dominio.Igrejaimport sistema.negocio.dominio.TipoEvento;import com.forj.cirrus.infra.spring.Escopo;import com.forj.cirrus.negocio.aplicativo.DominioBeanimport com.forj.cirrus.util.Cirrus;/** * Gerenciador de formulários GUI JSF de manutenção <b>TipoEvento</b>. * @author Lucas Francisquini * @version 1.0 - 27/11/2015 * @since 27/11/2015 */@Named
@Scope(Escopo.SESSION)
class TipoEventoFrm extends GListaDetalheFrm<TipoEvento> {    /** Armazena o serviço de negocio. **/    @Resource(name = Cirrus.FACADE)    DominioBean<TipoEvento> bean    /** Contrutor padrão. **/    TipoEventoFrm() {        titulo = "Manutenção de Tipo de Evento"        dominio = new TipoEvento()    }    /** {@inheritDoc} */    @Override    public void pesquisar() {        try {            colecao = bean.get(TipoEvento.TODOS)        } catch (ex) {            gerarErrosRia(ex)        }    }}